mapper.select:
SELECT t.order_id,a.email,t.charge/${money_rate} as charge,t.charge_type,t.sid, date_format( t.create_date, '%Y-%m-%d %H:%i:%S' ) AS create_date, t.status FROM tb_srv_payment_order t join tb_ucpaas_user a on t.sid = a.sid WHERE a.super_sid = #{superSid}
AND ( t.order_id LIKE concat('%', #{text}, '%') OR a.email LIKE concat('%', #{text}, '%') )
ORDER BY t.create_date DESC ${limit}
