mapper.select:
SELECT l.log_id, t.task_id, t.task_name, t.task_type, l.data_date, date_format( l.start_date, '%Y-%m-%d %H:%i:%S' ) AS start_date, date_format( l.end_date, '%Y-%m-%d %H:%i:%S' ) AS end_date, l.duration, l.remark, l.`status` FROM tb_ucpaas_task_log l, tb_ucpaas_task t WHERE l.task_id = t.task_id
AND ( l.log_id LIKE concat('%', #{text}, '%') OR l.data_date LIKE concat('%', #{text}, '%') )
AND l.start_date >= #{start_date}
AND l.start_date <= #{end_date}
AND t.task_id = #{task_id}
AND t.task_type = #{task_type}
AND l.duration IS NULL
AND l.duration = '00:00:00'
AND l.duration >= #{duration}
AND l.`status` = #{status}
ORDER BY l.start_date DESC ${limit}

SELECT
	count(0) AS totalCount
FROM
	tb_ucpaas_task_log l,
	tb_ucpaas_task t
WHERE
	l.task_id = t.task_id


	AND (
		l.log_id LIKE concat('%', #{text}, '%')
		OR l.data_date LIKE concat('%', #{text}, '%')
	)


	AND l.start_date >= #{start_date}


	 AND l.start_date <= #{end_date} 


	AND t.task_id = #{task_id}


	AND t.task_type = #{task_type}



		AND l.duration IS NULL


		AND l.duration = '00:00:00'


		AND l.duration >= #{duration}



	AND l.`status` = #{status}
