mapper.select:
SELECT
	t.task_id,
	t.task_name,
	t.procedure_name,
	t.`group`,
	t.`order`,
	t.execute_next,
	date_format(
		t.scan_next,
		'%Y-%m-%d %H:%i:%S'
	) AS scan_next,
	t.`status`
FROM
	tb_ucpaas_task t
WHERE
	1 = 1


	AND (
		t.task_id LIKE concat('%', #{text}, '%')
		OR t.task_name LIKE concat('%', #{text}, '%')
		OR t.procedure_name LIKE concat('%', #{text}, '%')
	)


	AND t.scan_next >= #{start_date}


	 AND t.scan_next <= #{end_date} 


	AND t.task_type = #{task_type}


	AND t.db_type = #{db_type}


	AND t.execute_type = #{execute_type}


	AND t.scan_execute = #{scan_execute}


	AND t.`group` = #{group}


	AND t.`status` = #{status}


ORDER BY
	t.scan_next,
	t.`group`,
	t.`order`
${limit}

SELECT
	count(0) AS totalCount
FROM
	tb_ucpaas_task t
WHERE
	1 = 1


	AND (
		t.task_id LIKE concat('%', #{text}, '%')
		OR t.task_name LIKE concat('%', #{text}, '%')
		OR t.procedure_name LIKE concat('%', #{text}, '%')
	)


	AND t.scan_next >= #{start_date}


	 AND t.scan_next <= #{end_date} 


	AND t.task_type = #{task_type}


	AND t.db_type = #{db_type}


	AND t.execute_type = #{execute_type}


	AND t.scan_execute = #{scan_execute}


	AND t.`group` = #{group}


	AND t.`status` = #{status}

mapper.update:
UPDATE tb_ucpaas_task t
SET t.`status` = #{status}
WHERE
	t.task_id = #{task_id}
