mapper.select:
SELECT t.* FROM ( SELECT b.acct_id, b.sid, u.email, u.realname, u.mobile, b.balance / ${money_rate} AS balance, b.credit_balance /${money_rate} AS credit_balance_fmt, p.package_name, b.enable_flag AS wallet_status, date_format( ( SELECT max(p.pay_date) FROM tb_srv_payment_order p WHERE p.sid = b.sid AND p.`status` = 2 ), '%Y-%m-%d %H:%i:%S' ) AS last_recharge_date FROM tb_bill_acct_balance b LEFT JOIN tb_ucpaas_user u ON u.sid = b.sid LEFT JOIN tb_bill_acct_package_rel pr ON pr.sid = b.sid LEFT JOIN tb_bill_package p ON p.package_id = pr.package_id WHERE u.is_proxy = 0 And u.user_type in (1,2)
AND ( b.acct_id LIKE concat(#{text}, '%') OR u.email LIKE concat(#{text}, '%') OR u.realname LIKE concat('%',#{text}, '%') )
AND b.balance >= #{start_balance} * ${money_rate}
AND b.balance <= #{end_balance} * ${money_rate}
AND b.enable_flag = #{wallet_status}
) t WHERE 1 = 1
AND t.last_recharge_date >= #{start_date}
AND t.last_recharge_date <= #{end_date}
ORDER BY t.last_recharge_date DESC ${limit}

SELECT count(0) AS totalCount FROM ( SELECT b.acct_id, b.sid, u.email, u.realname, u.mobile, b.balance / ${money_rate} AS balance, b.credit_balance /${money_rate} AS credit_balance_fmt, p.package_name, b.enable_flag AS wallet_status, date_format( ( SELECT max(p.pay_date) FROM tb_srv_payment_order p WHERE p.sid = b.sid AND p.`status` = 2 ), '%Y-%m-%d %H:%i:%S' ) AS last_recharge_date FROM tb_bill_acct_balance b LEFT JOIN tb_ucpaas_user u ON u.sid = b.sid LEFT JOIN tb_bill_acct_package_rel pr ON pr.sid = b.sid LEFT JOIN tb_bill_package p ON p.package_id = pr.package_id WHERE u.is_proxy = 0 And u.user_type in (1,2)
AND ( b.acct_id LIKE concat(#{text}, '%') OR u.email LIKE concat(#{text}, '%') OR u.realname LIKE concat('%',#{text}, '%') )
AND b.balance >= #{start_balance} * ${money_rate}
AND b.balance <= #{end_balance} * ${money_rate}
AND b.enable_flag = #{wallet_status}
) t WHERE 1 = 1
AND t.last_recharge_date >= #{start_date}
AND t.last_recharge_date <= #{end_date}
