mapper.select:
SELECT p.package_id, p.package_name, p1.param_value AS pay_type_name, p.`status`, p2.param_value AS status_name, p3.param_value AS package_type_name, ( SELECT i.fee FROM tb_bill_fee_item_rel ir, tb_bill_fee_item i WHERE ir.package_id = p.package_id AND i.fee_id = ir.fee_id AND i.fee_id LIKE '${event_id_1017}%' ) / ${money_rate} AS min_consumption, ( SELECT count(0) FROM tb_bill_acct_package_rel pr WHERE pr.package_id = p.package_id ) AS developer_number, date_format( p.create_date, '%Y-%m-%d %H:%i:%S' ) AS create_date, date_format( p.update_date, '%Y-%m-%d %H:%i:%S' ) AS update_date FROM tb_bill_package p LEFT JOIN tb_ucpaas_params p1 ON p1.param_key = p.charge_type AND p1.param_type = 'pay_type' LEFT JOIN tb_ucpaas_params p2 ON p2.param_key = p.`status` AND p2.param_type = 'package_status' LEFT JOIN tb_ucpaas_params p3 ON p3.param_key = p.package_type AND p3.param_type = 'package_type' WHERE 1 = 1
AND p.package_name LIKE concat('%', #{text}, '%')
AND p.package_type = #{package_type}
ORDER BY p.update_date DESC ${limit}

SELECT count(0) AS totalCount FROM tb_bill_package p WHERE 1 = 1
AND p.package_name LIKE concat('%', #{text}, '%')
AND p.package_type = #{package_type}

mapper.update:
UPDATE tb_bill_fee_item_rel ir
SET ir.fee_id = #{fee_id}
WHERE
	ir.package_id = #{package_id}
AND ir.fee_id = #{old_fee_id}

UPDATE tb_bill_package p
SET p.`status` = #{status}

 	, p.eff_date = now()


	, p.exp_date = now()

WHERE
	p.package_id = #{package_id}

UPDATE tb_bill_acct_package_rel pr
SET pr.package_id = #{new_package_id}
WHERE
	pr.package_id = #{package_id}


	AND pr.sid = #{sid}
