mapper.select:
SELECT t.id, u.sid, u.email, t.title, t.type, t.opentype, t.money/${money_rate} as money_fmt, date_format( t.update_date, '%Y-%m-%d %H:%i:%S' ) AS update_date_fmt, t.status, t.logistics_company, t.logistics_no, (SELECT a.audit_desc FROM tb_srv_audit a WHERE a.audited_id = t.id AND a.audit_type = 5 LIMIT 1) AS audit_desc FROM tb_srv_invoice t, tb_ucpaas_user u WHERE t.sid = u.sid
AND ( t.sid LIKE concat('%', #{text}, '%') OR u.email LIKE concat('%', #{text}, '%') OR u.mobile LIKE concat('%', #{text}, '%') )
AND t.update_date >= #{start_date}
AND t.update_date <= #{end_date}
AND t.status = #{status}
ORDER BY t.update_date DESC ${limit}

SELECT count(*) AS totalCount FROM tb_srv_invoice t, tb_ucpaas_user u WHERE t.sid = u.sid
AND ( t.sid LIKE concat('%', #{text}, '%') OR u.email LIKE concat('%', #{text}, '%') OR u.mobile LIKE concat('%', #{text}, '%') )
AND t.update_date >= #{start_date}
AND t.update_date <= #{end_date}
AND t.status = #{status}

mapper.update:
UPDATE tb_srv_invoice t
SET t.status = #{bill_status},t.update_date = now()
WHERE t.id = #{id}
