mapper.select:
SELECT o.* ,o.status as status, date_format( o.create_date, '%Y-%m-%d %H:%i:%S' ) AS create_date ,o.pay_id as pay_id from tb_srv_payment_order o LEFT JOIN tb_ucpaas_user u ON o.sid=u.sid LEFT JOIN tb_ucpaas_client c on c.sid=o.sid WHERE 1=1
AND ( o.charge_type LIKE concat(#{text}, '%') or o.order_id LIKE concat(#{text}, '%') or o.charge like concat(#{text}, '%') or o.status like concat(#{text}, '%') or o.sid like concat(#{text}, '%') )
AND o.charge_type = #{charge_type}
AND o.order_id = #{order_id}
AND o.charge = #{charge}
AND o.status = #{status}
ORDER BY o.order_id DESC ${limit}

SELECT o.*,u.* ,o.status as status, date_format(
o.create_date,
'%Y-%m-%d %H:%i:%S'
) AS create_date
from
tb_srv_payment_order o,
tb_ucpaas_user u
WHERE o.sid=u.sid



AND (	o.charge_type LIKE concat(#{text}, '%')
	OR o.order_id
	LIKE concat(#{text}, '%')
	OR u.email LIKE concat(#{text}, '%')
	OR
	u.username LIKE concat(#{text}, '%')
	OR u.mobile LIKE concat(#{text},
	'%')
	)




	AND o.order_type = #{order_type}



	AND u.username = #{username}


	AND o.order_id = #{order_id}


	AND o.create_date >= #{start_date}



	AND u.mobile = #{mobile}



	AND u.email = #{email}



	AND IF (o.`status` = 2, 1, 3, o.`status`) = #{status}


ORDER BY
o.order_id ASC
${limit}

SELECT
a.mobile, t.acct_id, t.client_number, b.app_sid, c.app_name, a.email, t.balance AS balance, e.package_name, date_format( t.create_time, '%Y-%m-%d %H:%i:%S' ) AS create_time, c.industry, t.enable_flag FROM tb_ucpaas_user a, tb_ucpaas_client b , tb_ucpaas_application c , tb_bill_acct_package_rel d , tb_bill_package e , tb_bill_client_balance t WHERE c.sid = a.sid and d.package_id = e.package_id and d.sid = a.sid and b.app_sid = c.app_sid and t.client_number = b.client_number
AND ( t.acct_id LIKE concat(#{text}, '%') OR b.client_number LIKE concat(#{text}, '%') OR b.mobile LIKE concat(#{text}, '%') OR t.sid LIKE concat(#{text},'%') OR c.app_name LIKE concat(#{text}, '%')
)
AND u.create_date >= #{start_date}
AND u.create_date<= #{end_date}
AND b.mobile = #{mobile}
and t.sid = (select sid from tb_ucpaas_user where email = #{email} limit 1)
AND c.app_name = #{app_name}
AND t.enable_flag = #{wallet_status}
AND c.industry = #{industry}
ORDER BY t.create_time DESC ${limit}

SELECT u.username, u.sid, app.app_sid, c.client_number, app.app_name, u.email AS developer_email, app.industry, c.`status`, c.mobile, date_format( u.create_date, '%Y-%m-%d %H:%i:%S' ) AS create_date FROM tb_ucpaas_user u,tb_ucpaas_application app,tb_ucpaas_client c WHERE u.sid = app.sid and app.app_sid = c.app_sid
AND ( c.client_number LIKE concat('%',#{text}, '%') OR app.app_name LIKE concat('%',#{text}, '%') OR u.username LIKE concat('%',#{text},'%') OR u.sid LIKE concat('%' ,#{text}, '%') OR app.app_sid LIKE concat('%' ,#{text}, '%') )
AND u.create_date >= #{start_date}
AND u.create_date<= #{end_date}
and app.industry = #{industry}
ORDER BY c.create_date DESC ${limit}

SELECT
	count(0) AS totalCount
FROM
	tb_ucpaas_user u,
	tb_ucpaas_application app,
	tb_ucpaas_client c
WHERE
	u.sid = app.sid and app.app_sid = c.app_sid


	AND (
		c.client_number LIKE concat('%',#{text}, '%')
		OR app.app_name LIKE concat('%',#{text}, '%')
		OR u.username LIKE concat('%',#{text},'%')
		OR u.sid LIKE concat('%' ,#{text}, '%')
		OR app.app_sid LIKE concat('%' ,#{text}, '%')
	)


	AND u.create_date >= #{start_date}


	 AND u.create_date<= #{end_date} 


	AND c.client_number = #{client_number}


	AND app.app_name = #{app_name}


	AND c.mobile = #{mobile}



	and c.sid = (select sid from tb_ucpaas_user where email =
	#{email} limit 1)


	AND IF (c.`status` = 2, 1, c.`status`) = #{status}


	and app.industry = #{industry}

select sid,token from tb_ucpaas_user where 1=1

	and email = #{email}


	and sid = #{sid}

and user_type != 3 limit 1
