mapper.select:
SELECT u.sid, u.email, u.mobile, u.username, u.realname, date_format( u.create_date, '%Y-%m-%d %H:%i:%S' ) AS create_date, u.`status`, u.oauth_status, u.is_heavybuyer, u.channel_id, b.enable_flag, u.is_proxy FROM tb_ucpaas_user u LEFT JOIN tb_bill_acct_balance b ON b.sid = u.sid WHERE u.user_type IN (1, 2)
AND ( u.sid LIKE concat(#{text}, '%') OR u.email LIKE concat(#{text}, '%') OR u.mobile LIKE concat(#{text}, '%') OR u.username LIKE concat('%',#{text}, '%') OR u.realname LIKE concat('%',#{text}, '%') )
AND u.create_date >= #{start_date}
AND u.create_date <= #{end_date}
AND u.`status` = #{user_status}
AND u.oauth_status = #{oauth_status}
AND b.enable_flag = #{wallet_status}
ORDER BY u.create_date DESC ${limit}

SELECT
	count(0) AS totalCount
FROM
	tb_ucpaas_user u
LEFT JOIN tb_bill_acct_balance b ON b.sid = u.sid
WHERE
	u.user_type IN (1, 2)


	AND (
		u.sid LIKE concat(#{text}, '%')
		OR u.email LIKE concat(#{text}, '%')
		OR u.mobile LIKE concat(#{text}, '%')
		OR u.username LIKE concat('%',#{text}, '%')
		OR u.realname LIKE concat('%',#{text}, '%')
	)



	AND u.create_date >= #{start_date}


	 AND u.create_date <= #{end_date} 


	AND u.`status` = #{user_status}


	AND u.oauth_status = #{oauth_status}


	AND b.enable_flag = #{wallet_status}

mapper.update:
UPDATE tb_srv_remind r
SET r.money = #{remind_money} * 1000000,
	r.remind_admin = #{remind_admin}
WHERE
	r.sid = #{sid}
AND r.remind_type = 1

UPDATE tb_ucpaas_user u
SET u.`status` = #{status}
WHERE
	u.user_type IN (1,2)
AND u.sid = #{sid}

UPDATE tb_ucpaas_application a
SET a.`status` = #{app_status}
WHERE
	a.sid = #{sid}

UPDATE tb_ucpaas_client c
SET c.`status` = #{client_status}
WHERE
	c.sid = #{sid}

UPDATE tb_bill_acct_balance b
SET b.enable_flag = #{wallet_status}
WHERE
	b.sid = #{sid}

UPDATE tb_ucpaas_user u
SET u.is_heavybuyer = #{is_heavybuyer}
WHERE
	u.sid = #{sid}

UPDATE tb_ucpaas_user u
SET u.is_proxy = #{isProxy}
WHERE
	u.sid = #{sid}
