mapper.select:
SELECT app.app_sid, app.app_name, app.app_kind, app.status, app.create_date, ifnull(blc.balance,0)/1000000 as balance, blc.status as blcStatus, client.cli_num FROM tb_ucpaas_application app LEFT JOIN tb_ucpaas_app_balance blc ON (app.app_sid=blc.app_sid) LEFT JOIN (SELECT COUNT(*) cli_num,app_sid FROM tb_ucpaas_client GROUP BY app_sid) client ON app.app_sid = client.app_sid where app.sid=#{sid} and app.status!='3' and app.app_type='1'
and app.app_name like concat('%',#{appName},'%')
ORDER BY create_date DESC ${limit}

	SELECT *
	FROM t_rs_sess_snapshoot_${dateTime}
	WHERE cookie = #{cookie}

        AND sid = #{userSid}


        AND appid = #{appSid}
