mapper.select:
select * from tb_rs_oprate_log,tb_rs_user_info
where tb_rs_user_info.sid  in (
	select sid from tb_rs_user_info 
	where net_sid = #{netSid,jdbcType=VARCHAR}  
	and tb_rs_user_info.sid = tb_rs_oprate_log.sid
	and tb_rs_oprate_log.create_date between #{dateMin,jdbcType=VARCHAR} and #{dateMax,jdbcType=VARCHAR}
)order by log_id desc limit #{begin,jdbcType=INTEGER} , #{count,jdbcType=INTEGER}

select count(*) from tb_rs_oprate_log,tb_rs_user_info 
where tb_rs_user_info.sid  in (
	select sid from tb_rs_user_info 
	where net_sid = #{netSid,jdbcType=VARCHAR} 
	and tb_rs_user_info.sid = tb_rs_oprate_log.sid
	and tb_rs_oprate_log.create_date between #{dateMin,jdbcType=VARCHAR} and #{dateMax,jdbcType=VARCHAR}
)
