mapper.select:
SELECT l.log_id, u.sid, u.email, u.realname, l.page_url, l.op_type, l.op_desc, l.ip, date_format( l.create_date, '%Y-%m-%d %H:%i:%S' ) AS create_date FROM ucpaas.tb_ucpaas_oprate_log l, ucpaas.tb_ucpaas_admin u WHERE l.sid = u.sid
AND ( u.email LIKE concat('%', #{text}, '%') OR l.page_url LIKE concat('%', #{text}, '%') OR l.op_desc LIKE concat('%', #{text}, '%') OR l.ip LIKE concat('%', #{text}, '%') )
AND l.create_date >= #{start_date}
AND l.create_date <= #{end_date}
AND l.op_type = #{op_type}
ORDER BY l.create_date DESC ${limit}

SELECT count(0) AS totalCount FROM ucpaas.tb_ucpaas_oprate_log l, ucpaas.tb_ucpaas_admin u WHERE l.sid = u.sid
AND ( u.email LIKE concat('%', #{text}, '%') OR l.page_url LIKE concat('%', #{text}, '%') OR l.op_desc LIKE concat('%', #{text}, '%') OR l.ip LIKE concat('%', #{text}, '%') )
AND l.create_date >= #{start_date}
AND l.create_date <= #{end_date}
AND l.op_type = #{op_type}
